mapper.select:
SELECT *
FROM items


        AND LOWER(name) like concat('%', LOWER(#{name}), '%')


        AND price >= #{price}


        AND warehouse_id = #{warehouseId}


ORDER BY price ${orderBy}
LIMIT #{size} OFFSET #{size} * (#{page} - 1)

SELECT COUNT(*) FROM items


        AND LOWER(name) like concat('%', LOWER(#{name}), '%')


        AND price >= #{price}


        AND warehouse_id = #{warehouseId}

mapper.update:
UPDATE items
price = #{price}, warehouse_id = #{warehouseId}, name = #{name}, quantity = #{quantity}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}
